import React, { useEffect, useState } from 'react';
import '../styles/steven.css';

const Phil = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    // After the component mounts, trigger the slide-up effect
    setIsVisible(true);
  }, []);

  return (
    <div className={`phil-container ${isVisible ? 'slide-up' : ''}`}>
      <div className="phil-box">
        <img
          src={`${process.env.PUBLIC_URL}/phil.png`}
          alt="Phil"
          className="phil-image"
        />
        <div className="phil-text">
          <h2>Phil</h2>
          <p>
            Passionate top-notch FPS streamer. With his versatile experience in different platforms and PC shooting games.
            His aim and game sense is unmatchable. His love for basketball could be an influence on his gaming ability.
          </p>
        </div>
      </div>
    </div>
  );
};

export default Phil;
